mapper.select:
SELECT * FROM address_book


        AND user_id = #{userId}


        AND phone = #{phone}


        AND is_default = #{isDefault}

mapper.update:
UPDATE address_book
consignee = #{consignee}, sex = #{sex}, phone = #{phone}, province_code = #{provinceCode}, province_name = #{provinceName}, city_code = #{cityCode}, city_name = #{cityName}, district_code = #{districtCode}, district_name = #{districtName}, detail = #{detail}, label = #{label}, is_default = #{isDefault},
WHERE id = #{id}
