mapper.select:
SELECT * FROM category
AND name LIKE CONCAT('%', #{name}, '%') AND type = #{type}
ORDER BY sort ASC, create_time DESC

mapper.update:
UPDATE category
type = #{type}, name = #{name}, sort = #{sort}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
WHERE id = #{id}
