mapper.select:
SELECT s.*, c.name AS category_name FROM setmeal s INNER JOIN category c ON s.category_id = c.id


        AND s.name LIKE CONCAT('%', #{name}, '%')


        AND s.category_id = #{categoryId}


        AND s.status = #{status}


ORDER BY s.create_time DESC

mapper.update:
UPDATE setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, description = #{description}, image = #{image}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
WHERE id = #{id}
